part1 :: String -> Int
part1 = foldl folder 0
  where
    folder acc '(' = acc + 1
    folder acc _ = acc - 1

part2 :: String -> Int
part2 = fst . foldl folder (0, 0)
  where
    folder acc@(_, -1) _ = acc
    folder (index, floor) '(' = (index + 1, floor + 1)
    folder (index, floor) _ = (index + 1, floor - 1)
